import React, { useState, useEffect } from 'react';
import { Route, Routes, Navigate } from 'react-router-dom';
import { auth } from './firebase';
import { onAuthStateChanged } from 'firebase/auth';
import Home from './Home';
import Login from './Login';
import SignUp from './SignUp';
import Dashboard from './Dashboard';
import Profile from './Profile';
import CombinedPosts from './CombinedPosts';
import Question from './Question';
import QuestionList from './QuestionList';
import QuestionDetails from './QuestionDetails';
import Osusume from './Osusume';
import Chat from './Chat';

function App() {
  const [userId, setUserId] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUserId(user.uid);
      } else {
        setUserId(null);
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/login" element={<Login />} />
      <Route path="/signup" element={<SignUp />} />
      <Route path="/dashboard" element={<Dashboard />} />
      <Route path="/profile" element={<Profile />} />
      <Route path="/combinedposts" element={<CombinedPosts />} />
      <Route path="/questions" element={<QuestionList />} />
      <Route
        path="/questions/add"
        element={userId ? <Question userId={userId} /> : <Navigate to="/login" />} // ログイン時のみ表示
      />
      <Route path="/questiondetail/:id" element={<QuestionDetails userId={userId} />} />
      <Route path="/osusume" element={<Osusume />} />
      <Route path="/chat" element={<Chat />} />
      <Route path="/add" element={<Chat />} />
    </Routes>
  );
}

export default App;
