import React, { useEffect, useState } from 'react';
import { db } from './firebase';
import { collection, getDocs } from 'firebase/firestore';

function ThreeChoiceQuestion({ onAnswerSubmit }) {
  const [questions, setQuestions] = useState([]); // 初期値を空配列に設定
  const [selectedAnswers, setSelectedAnswers] = useState({}); // ユーザーの選択を保存

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const questionsSnapshot = await getDocs(collection(db, 'ThreeChoiceQuestions'));
        const questionsData = questionsSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        }));
        setQuestions(questionsData);
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };
    fetchQuestions();
  }, []);

  const handleAnswerChange = (questionId, choice) => {
    setSelectedAnswers(prev => ({ ...prev, [questionId]: choice }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAnswerSubmit(selectedAnswers); // 親コンポーネントに回答を渡す
  };

  return (
    <div>
      <h2>価値観確認 - 三択質問</h2>
      <form onSubmit={handleSubmit}>
        {questions.length > 0 ? (
          questions.map(question => (
            <div key={question.id}>
              <h3>{question.text}</h3>
              {question.choices.map(choice => (
                <label key={choice}>
                  <input
                    type="radio"
                    name={question.id}
                    value={choice}
                    checked={selectedAnswers[question.id] === choice}
                    onChange={() => handleAnswerChange(question.id, choice)}
                  />
                  {choice}
                </label>
              ))}
            </div>
          ))
        ) : (
          <p>Loading questions...</p> // 質問がロード中の場合の表示
        )}
        <button type="submit">回答を送信</button>
      </form>
    </div>
  );
}

export default ThreeChoiceQuestion;
