import { checkMutualLike } from './checkMutualLike';
import { saveMatch } from './saveMatch';
import { collection, getDocs } from 'firebase/firestore';
import { db } from './firebase';

export async function matchUsers(userId) {
  console.log(`--- マッチング開始 ---`);
  console.log(`マッチングをチェック中: 使用ユーザー ${userId} と全ユーザー`);

  try {
    // データベースから全ユーザーを取得
    const usersSnapshot = await getDocs(collection(db, 'users'));
    const allUsers = usersSnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));

    // 全ユーザーに対してマッチングを確認
    for (const otherUser of allUsers) {
      const otherUserId = otherUser.id;

      // 自分自身とのマッチングを避ける
      if (otherUserId === userId) continue;

      // 各ユーザーIDが `undefined` でないかをチェック
      if (!userId || !otherUserId) {
        console.warn(`無効なユーザーID: userId: ${userId}, otherUserId: ${otherUserId}`);
        continue;
      }

      // マッチング確認
      const likesFromUser = await checkMutualLike(userId, otherUserId);
      const likesToUser = await checkMutualLike(otherUserId, userId);

      console.log(`いいね確認結果: ${userId} -> ${otherUserId}: ${likesFromUser}, ${otherUserId} -> ${userId}: ${likesToUser}`);

      // 双方から「いいね」がある場合のみマッチを保存
      if (likesFromUser && likesToUser) {
        try {
          await saveMatch(userId, otherUserId);
          console.log(`新しいマッチが保存されました: ${userId} と ${otherUserId}`);
        } catch (error) {
          console.error(`マッチング保存エラー: ${userId} と ${otherUserId}`, error);
        }
      } else {
        console.log(`マッチング不可: 双方のいいねが不足 (${userId} -> ${otherUserId}: ${likesFromUser}, ${otherUserId} -> ${userId}: ${likesToUser})`);
      }
    }
  } catch (error) {
    console.error('マッチングエラー:', error);
  }
}
