import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { db } from './firebase';
import { collection, addDoc, query, where, onSnapshot } from 'firebase/firestore';
import './st.css'; // CSSファイルをインポート

function Chat({ userId }) {
  const { chatId } = useParams();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    const messagesQuery = query(collection(db, 'Chats', chatId, 'Messages'));
    const unsubscribe = onSnapshot(messagesQuery, (snapshot) => {
      setMessages(snapshot.docs.map(doc => doc.data()));
    });
    return unsubscribe;
  }, [chatId]);

  const sendMessage = async () => {
    if (newMessage.trim()) {
      await addDoc(collection(db, 'Chats', chatId, 'Messages'), {
        userId,
        text: newMessage,
        timestamp: new Date(),
      });
      setNewMessage('');
    }
  };

  return (
    <div className="chat-container">
      <h2>チャットルーム</h2>
      <div className="message-list">
        {messages.map((msg, index) => (
          <div 
            key={index} 
            className={`message ${msg.userId === userId ? 'my-message' : 'other-message'}`}
          >
            <strong>{msg.userId === userId ? 'あなた' : msg.userId}:</strong> {msg.text}
          </div>
        ))}
      </div>
      <div className="input-container">
        <input 
          className="message-input" 
          value={newMessage} 
          onChange={(e) => setNewMessage(e.target.value)} 
          placeholder="メッセージを入力..."
        />
        <button className="send-button" onClick={sendMessage}>送信</button>
      </div>
    </div>
  );
}

export default Chat;
