import React, { useEffect, useState } from 'react';
import { db } from './firebase';
import { useParams } from 'react-router-dom';
import { doc, getDoc, collection, query, where, getDocs } from 'firebase/firestore';
import Answer from './Answer';

function QuestionDetails() {
    const { id } = useParams();
    const [question, setQuestion] = useState(null);
    const [answers, setAnswers] = useState([]);
    const [loading, setLoading] = useState(true); // ローディング状態を管理
    const [error, setError] = useState(null); // エラーメッセージを管理

    useEffect(() => {
        const fetchQuestionAndAnswers = async () => {
            setLoading(true); // ローディング状態開始
            try {
                console.log("Fetching question with ID:", id);

                // 特定のIDの質問を取得
                const questionDoc = await getDoc(doc(db, 'Questions', id));
                if (questionDoc.exists()) {
                    const questionData = questionDoc.data();
                    setQuestion(questionData);
                    console.log("Question found:", questionData);

                    // 質問に関連する回答を取得
                    const answersQuery = query(collection(db, 'Answers'), where('questionId', '==', id));
                    const answersSnapshot = await getDocs(answersQuery);
                    setAnswers(answersSnapshot.docs.map(doc => doc.data()));
                } else {
                    console.warn("Question not found");
                    setError("質問が見つかりませんでした。");
                }
            } catch (error) {
                console.error("Error fetching data:", error);
                setError("データの取得中にエラーが発生しました。");
            } finally {
                setLoading(false); // ローディング状態終了
            }
        };

        fetchQuestionAndAnswers();
    }, [id]);

    if (loading) {
        return <p>Loading question...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    return (
        <div>
            {question ? (
                <>
                    <h2>{question.question}</h2>
                    <h3>回答一覧</h3>
                    {answers.length > 0 ? (
                        <ul>
                            {answers.map((answer, index) => (
                                <li key={index}>{answer.answer}</li>
                            ))}
                        </ul>
                    ) : (
                        <p>まだ回答がありません。</p>
                    )}
                    {/* 回答を投稿するコンポーネント */}
                    <Answer questionId={id} />
                </>
            ) : (
                <p>質問が見つかりません。</p>
            )}
        </div>
    );
}

export default QuestionDetails;
