import React from 'react';
import { useNavigate } from 'react-router-dom';

function MatchedUsers({ userId }) {
  const [matches, setMatches] = useState([]);
  const navigate = useNavigate();

  // チャットページへの遷移関数
  const goToChat = async (matchedUserId) => {
    const chatId = await getOrCreateChat(userId, matchedUserId); // 事前に作成した関数を利用
    navigate(`/chat/${chatId}`);
  };

  return (
    <div>
      <h2>マッチしたユーザー</h2>
      <ul>
        {matches.map((match, index) => (
          <li key={index}>
            <p>マッチユーザー名: {match.matchedUserName}</p>
            <button onClick={() => goToChat(match.matchedUserId)}>チャットする</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
