import { doc, updateDoc, arrayUnion } from 'firebase/firestore';

async function sendMessage(chatId, senderId, text) {
  const chatRef = doc(db, 'Chats', chatId);

  await updateDoc(chatRef, {
    messages: arrayUnion({
      sender: senderId,
      text: text,
      timestamp: new Date()
    })
  });
}
