import React, { useState, useEffect } from 'react';
import { db } from './firebase';
import { collection, addDoc, query, where, getDocs } from 'firebase/firestore';

async function postAnswer(questionId, userId, answerText) {
    try {
        await addDoc(collection(db, 'answers'), {
            questionId: questionId,
            userId: userId,
            answer: answerText,
            createdAt: new Date(),
        });
        console.log('回答が投稿されました');
    } catch (error) {
        console.error('エラー発生:', error);
    }
}

function Answer({ questionId, userId, questionText }) {
    const [answerText, setAnswerText] = useState('');
    const [answers, setAnswers] = useState([]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await postAnswer(questionId, userId, answerText);
        setAnswerText('');
        fetchAnswers(); // 投稿後に再度回答を取得
    };

    const fetchAnswers = async () => {
        const q = query(collection(db, 'answers'), where('questionId', '==', questionId));
        const querySnapshot = await getDocs(q);
        const answersData = querySnapshot.docs.map(doc => doc.data());
        setAnswers(answersData);
    };

    useEffect(() => {
        if(questionId) {
            fetchAnswers();
        }
    }, []);

    return (
        <div>
            <h2>質問: {questionText}</h2>
            <form onSubmit={handleSubmit}>
                <textarea
                    placeholder="回答を入力してください"
                    value={answerText}
                    onChange={(e) => setAnswerText(e.target.value)}
                    required
                />
                <button type="submit">回答を投稿</button>
            </form>

            <h3>過去の回答</h3>
            <ul>
                {answers.map((answer, index) => (
                    <li key={index}>{answer.answer}</li>
                ))}
            </ul>
        </div>
    );
}

export default Answer;
